}

.text-3xl {
    font-size: var(--text-3xl);
}

.text-4xl {
    font-size: var(--text-4xl);
}

.text-5xl {
    font-size: var(--text-5xl);
}

.text-6xl {
    font-size: var(--text-6xl);
}

.text-xl {
    font-size: var(--text-xl);
}

.text-lg {
    font-size: var(--text-lg);
}

.text-sm {
    font-size: var(--text-sm);
}

.mb-2 {
    margin-bottom: var(--spacing-2);
}

.mb-3 {
    margin-bottom: var(--spacing-3);
}

.mb-12 {
    margin-bottom: var(--spacing-12);
}

.gap-2 {
    gap: var(--spacing-2);
}

.gap-12 {
    gap: var(--spacing-12);
}

.block {
    display: block;
}

.gradient-text {
    background: linear-gradient(135deg, var(--color-brand-accent) 0%, #f4e4bc 50%, var(--color-brand-accent) 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    display: inline-block;
}


/* Header Navigation Alignment */

header nav .flex {
    display: flex;
    align-items: center;
}

header nav ul {
    display: flex;
    align-items: center;
}


/* Navigation Link Styles */

header nav ul li a,
header .btn-gradient {
    /* use identical padding */
