}

.footer-link:hover {
    color: var(--color-text-primary);
}

/* LUXURY BOUTIQUE AESTHETIC - COMMANDING EXECUTIVE PRESENCE */

.btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 40px rgba(201, 169, 97, 0.4);
    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.card:hover {
    transform: translateY(-8px);
    box-shadow: 0 25px 80px rgba(0, 0, 0, 0.3), 0 0 0 1px rgba(201, 169, 97, 0.2);
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    border-color: rgba(201, 169, 97, 0.3);
}

/* EXECUTIVE-LEVEL VISUAL HIERARCHY */
.card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
}

.card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(90deg, transparent, var(--color-brand-accent), transparent);
    opacity: 0;
    transition: opacity 0.3s ease;
}

.card:hover::before {
    opacity: 1;
}

.btn {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    text-transform: uppercase;
    letter-spacing: 1px;
    font-weight: 700;
    position: relative;
    overflow: hidden;
}

.btn::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%);
    transition: all 0.5s ease;
    transform: translate(-50%, -50%);
}

.btn:hover::after {
    width: 300px;
    height: 300px;
}

/* PREMIUM SECTION SPACING */
.section {
    padding: 8rem 0;
}

/* EXECUTIVE HERO SECTION */
#home {
    background: radial-gradient(ellipse at center, rgba(26, 26, 26, 0.8) 0%, var(--color-bg-primary) 70%);
    position: relative;
}

/* ELEGANT SCROLL INDICATOR */
.hero__scroll-indicator {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    opacity: 0.6;
    color: var(--color-brand-accent);
    animation: elegantBounce 2.5s ease-in-out infinite;
    cursor: pointer;
    transition: opacity 0.3s ease;
    z-index: 10;
}

.hero__scroll-indicator:hover {
    opacity: 1;
}

@keyframes elegantBounce {
    0%, 100% { 
        transform: translate(-50%, 0); 
        opacity: 0.6;
    }
    50% { 
        transform: translate(-50%, 8px); 
        opacity: 1;
    }
}

#home::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        linear-gradient(45deg, transparent 49%, rgba(201, 169, 97, 0.03) 50%, transparent 51%),
        linear-gradient(-45deg, transparent 49%, rgba(201, 169, 97, 0.03) 50%, transparent 51%);
    background-size: 60px 60px;
    pointer-events: none;
    opacity: 0.5;
}

/* STATS SECTION LUXURY STYLING */
.stat-item {
    text-align: center;
    padding: 2rem;
    background: rgba(26, 26, 26, 0.5);
    border-radius: var(--radius-xl);
    border: 1px solid rgba(201, 169, 97, 0.1);
    transition: all 0.3s ease;
}

.stat-item:hover {
    transform: translateY(-5px);
    border-color: rgba(201, 169, 97, 0.3);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
}

/* SOPHISTICATED TALENT GRID */
.talent-grid {
    gap: 2.5rem;
    margin: 4rem 0;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    align-items: stretch;
}

/* PREMIUM TALENT CARDS */
.talent-grid .card {
    height: 400px;
    background: linear-gradient(145deg, var(--color-bg-secondary) 0%, rgba(26, 26, 26, 0.8) 100%);
    backdrop-filter: blur(20px);
    display: flex;
    flex-direction: column;
}

/* ENSURE UNIFORM CARD CONTENT ALIGNMENT */
.talent-grid .card > div {
    flex: 1;
    display: flex;
    flex-direction: column;
}

/* TALENT CARD TEXT OVERLAY CONSISTENCY */
.talent-grid .card h3 {
    font-size: clamp(1.1rem, 2.5vw, 1.3rem) !important;
    line-height: 1.2 !important;
    max-height: 2.4em;
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
}

.talent-grid .card p {
    font-size: 0.85rem !important;
    line-height: 1.3 !important;
    height: 1.3rem;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

/* EXECUTIVE FOOTER STYLING */
footer {
    border-top: 2px solid var(--color-brand-accent) !important;
    background: linear-gradient(135deg, var(--color-bg-primary) 0%, var(--color-bg-secondary) 100%) !important;
    position: relative;
}

footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, var(--color-brand-accent), transparent);
    opacity: 0.8;
}

/* LUXURY GLASS EFFECTS */
.glass-effect {
    background: rgba(0, 0, 0, 0.85);
    backdrop-filter: blur(20px) saturate(180%);
    border-bottom: 1px solid rgba(201, 169, 97, 0.1);
}

/* PREMIUM HOVER STATES FOR ALL INTERACTIVE ELEMENTS */
a:not(.btn):hover {
    color: var(--color-brand-accent);
    transition: color 0.3s ease;
}

/* EXECUTIVE LEVEL MICRO-ANIMATIONS */
@keyframes luxuryPulse {
    0%, 100% { opacity: 0.8; }
    50% { opacity: 1; }
}

.text-gradient {
    animation: luxuryPulse 3s ease-in-out infinite;
