    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.card > * {
    flex-shrink: 0;
}

.card .card__body {
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

/* Home page talent grid - Premium layout with centered rows */
.talent-grid-home {
    display: flex;
    flex-wrap: wrap;
    gap: 2rem;
    justify-content: center;
}

/* Create two centered rows */
.talent-grid-home::after {
    content: "";
    width: 100%;
    order: 4;
}

/* All cards have consistent width */
.talent-grid-home .card {
    flex: 0 0 calc(25% - 1.5rem);
    max-width: calc(25% - 1.5rem);
}

/* Force break after 4th card and center the second row */
.talent-grid-home .card:nth-child(4) {
    margin-right: auto;
}

.talent-grid-home .card:nth-child(5) {
    margin-left: calc(12.5% - 0.5rem);
    flex: 0 0 calc(25% - 1.5rem);
}

.talent-grid-home .card:nth-child(7) {
    margin-right: calc(12.5% - 0.5rem);
}

/* Premium talent card with hover overlay */
.talent-grid-home .card {
    position: relative;
    height: 400px;
    cursor: pointer;
    overflow: hidden;
    border-radius: var(--radius-lg);
}

.talent-card-wrapper {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    border-radius: var(--radius-lg);
}

/* Image container with zoom effect */
.talent-card-image {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    transition: transform 0.6s cubic-bezier(0.23, 1, 0.32, 1);
}

.talent-grid-home .card:hover .talent-card-image {
    transform: scale(1.05);
}

/* Premium overlay with glass morphism */
.talent-card-overlay {
    position: absolute;
    bottom: -100%;
    left: 0;
    right: 0;
    height: 100%;
    background: linear-gradient(to top,
        rgba(0, 0, 0, 0.95) 0%,
        rgba(0, 0, 0, 0.85) 40%,
        rgba(0, 0, 0, 0.4) 70%,
        transparent 100%);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    transition: bottom 0.5s cubic-bezier(0.23, 1, 0.32, 1);
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: var(--spacing-6);
}

.talent-grid-home .card:hover .talent-card-overlay {
    bottom: 0;
}

/* Premium content styling */
.talent-overlay-content {
    transform: translateY(20px);
    opacity: 0;
    transition: all 0.5s cubic-bezier(0.23, 1, 0.32, 1) 0.1s;
}

.talent-grid-home .card:hover .talent-overlay-content {
    transform: translateY(0);
    opacity: 1;
}

.talent-overlay-content h3 {
    font-size: var(--text-2xl);
    margin-bottom: var(--spacing-3);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: white;
    font-weight: 300;
}

.talent-overlay-content .bio {
    font-size: var(--text-sm);
    line-height: 1.6;
    margin-bottom: var(--spacing-5);
    color: rgba(255, 255, 255, 0.8);
    font-weight: 300;
}

/* Elegant metrics display */
.talent-metrics {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-4);
    padding-top: var(--spacing-4);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.metric-item {
    text-align: left;
}

.metric-value {
    font-size: var(--text-xl);
    font-weight: 300;
    color: #ffffff;
    display: block;
    margin-bottom: 2px;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5);
}

.metric-label {
    font-size: 10px;
    color: rgba(255, 255, 255, 0.7);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    font-weight: 400;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
}

/* View Profile button */
.talent-view-profile {
    position: absolute;
    top: var(--spacing-4);
    right: var(--spacing-4);
    padding: var(--spacing-2) var(--spacing-4);
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: var(--radius-full);
    color: white;
    font-size: var(--text-xs);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    opacity: 0;
    transform: translateX(20px);
    transition: all 0.4s cubic-bezier(0.23, 1, 0.32, 1) 0.2s;
    text-decoration: none;
}

.talent-grid-home .card:hover .talent-view-profile {
    opacity: 1;
    transform: translateX(0);
}

.talent-view-profile:hover {
    background: rgba(255, 255, 255, 0.2);
    border-color: rgba(255, 255, 255, 0.4);
}

/* Responsive adjustments for home talent grid */
@media (max-width: 1200px) {
    .talent-grid-home .card {
        flex: 0 0 calc(33.333% - 1.5rem);
        max-width: calc(33.333% - 1.5rem);
    }

    .talent-grid-home .card:nth-child(5) {
        margin-left: 0;
    }

    .talent-grid-home .card:nth-child(7) {
        margin-right: 0;
    }
}

@media (max-width: 768px) {
    .talent-grid-home .card {
        flex: 0 0 calc(50% - 1rem);
        max-width: calc(50% - 1rem);
    }

    .talent-grid-home .card:nth-child(4) {
        margin-right: 0;
    }
}

@media (max-width: 640px) {
    .talent-grid-home {
        gap: 1.5rem;
    }

    .talent-grid-home .card {
        flex: 0 0 100%;
        max-width: 100%;
        height: 360px;
    }
}

/* Talent Profile Page - Enhanced text visibility */
.glass-effect {
    background: rgba(255, 255, 255, 0.03) !important;
    border: 1px solid rgba(255, 255, 255, 0.1) !important;
}

/* Stats and metrics on profile pages */
.glass-effect h3,
.glass-effect .text-4xl,
.glass-effect .gradient-text {
    color: var(--color-brand-primary) !important;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.glass-effect p,
.glass-effect span {
    color: rgba(255, 255, 255, 0.9) !important;
}

/* Platform cards */
.glass-effect .flex.items-center p:first-child {
    color: #ffffff !important;
    font-weight: 600;
}

/* Follower counts and stats */
.glass-effect .text-right p:first-child {
    color: var(--color-brand-primary) !important;
    font-weight: 700;
}
